import React,{Component} from 'react';

import './App.css';
import Header from './Component/Header/Header';
import Button from './Component/Button/Button';
import Column from './Component/Column/Column'


class App extends Component{



state = {
  veg:[
    {name: "sprout", age: 2},
    {name: "soy bean", age: 6},
    {name: "spinach", age: 4},
  ]
}



clickBtn = ()=>{ // function
  console.log("Button clicked");

  this.setState(
    {
      veg:[
        {name: "lavender", age: 12},
        {name: "rosemary", age: 7},
        {name: "Garlic", age: 11},
      ]
    }

  );

}



  render(){
    return (
      <div className="App">
         <h1 className="en">Millennail Supermarket</h1>
         <h1 className="cn">千禧超级市场</h1>
         <button className = "Btn" onClick = {this.clickBtn}>Change DATA</button> 
         {/* refer to the function */}


         <Column name = 'A' url = 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/27/Square_200x200.svg/200px-Square_200x200.svg.png'/>
         <Column name = 'B' url = 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/27/Square_200x200.svg/200px-Square_200x200.svg.png'/>
         <Column name = 'C' url = 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/27/Square_200x200.svg/200px-Square_200x200.svg.png'/>


      </div>
    );
  }

}



