state = {
  veg:[
    {name: "sprout", age: 2},
    {name: "soy bean", age: 6},
    {name: "spinach", age: 4},
  ]
}



clickBtn = ()=>{ // function
  console.log("Button clicked");

  this.setState(
    {
      veg:[
        {name: "lavender", age: 12},
        {name: "rosemary", age: 7},
        {name: "Garlic", age: 11},
      ]
    }

  );

}



